// I. Validate Company Information	
function ValidateCompany() {
	var errormsg = $("#company_error");
	errormsg.html("");
	var name = $("#name").val();
	var cik = $("#cik").val();		
	var fyr = $("#fyr").val();		
	var irs = $("#irs").val();
	var sic = $("#sic_enter").val();
	var name_patt = /^[A-Za-z0-9\.\' \-]{3,30}$/;
	var cik_patt = /^[0-9]{7}$/;
	var fyr_patt = /^((01|03|05|07|08|10|12)-([012][0-9]|3[01]))|((04|06|09|11)-([012][0-9]|30))|(02-([012][0-9]))$/;
	var dat_patt = /^([0-9]{2})-([0-9]{2})$/;
	var irs_patt = /^[0-9]{9}$/;
	var sic_patt = /^[0-9]{4}$/;
	name = name.toUpperCase(); 
	$("#name").val(name);
	if (name.length==0) errormsg.html("Please Enter Company Name");	// name
	else if (name_patt.test(name)==false) errormsg.html("Please Enter A Valid Company Name");	
	if (cik.length==0) errormsg.html("Please Enter Central Index Number"); // cik
	else if (cik_patt.test(cik)==false) errormsg.html("Please Enter 7-Digit Central Index Number");		
	if (fyr.length==0) errormsg.html("Please Enter Fiscal Year End"); // fyr
	else if (dat_patt.test(fyr)==false) errormsg.html("Please Enter Fiscal Year End In This Form: MM-DD");
	else if (fyr_patt.test(fyr)==false) errormsg.html("Please Enter A Valid Date");
	if (irs.length==0) errormsg.html("Please Enter IRS Number"); // irs
	else if (irs_patt.test(irs)==false) errormsg.html("Please Enter 9-Digit IRS Number"); 
	if (sic=="") errormsg.html("Please Select Or Enter SIC Code"); // sic
	else if (sic_patt.test(sic)==false) errormsg.html("Please Select Industry Or Enter A Valid SIC Code");
	else errormsg.html();
	return;
}

