// ajax to check if user exists
// example: http://www.ibm.com/developerworks/library/wa-aj-codeigniter/
function check_user() {
	$.post(
		'http://cs-server.usc.edu:9184/myhomework5/index.php/check_userid',
		{'username': $("#userid").val()},
		function(result) {
			if (result==1) {
				$("#user_exist").html("User ID Already Exist");
				$("#user_exist").css("color","red");
			}
		}
	);
}

